import React, { Component } from 'react';
import './App.css';
import AddPost from './components/AddPost';
//import Person from './Person/Person';
import FullPost from './components/FullPost';
import Post from './components/Post';
import Button from '@material-ui/core/Button';
import AppBar from '@material-ui/core/AppBar';
import Todo from './components/Todo';
import Header from './components/Header';
import Auth from './components/Auth';
import axios from 'axios';

class App extends Component {

  state = {
    posts: [
      { id: 1, title: 'T1', content: 'C1', image_path: '' },
      { id: 2, title: 'T2', content: 'C2', image_path: '' },
      { id: 3, title: 'T3', content: 'C3', image_path: '' },
      { id: 4, title: 'T4', content: 'C4', image_path: '' }
    ],
    selectedPostId: null
  }

  componentDidMount() {
    axios.get('https://blog-e314c.firebaseio.com/posts.json')
      .then(res => {
        const data = res.data;

        const posts = data.map(post => {
         return {title: item.title};
      });

        console.log('RES: ', posts);
      }).catch(err => {
        console.log('ERR: ', err);
      });
  }

  postDataHundler = (newPost) => {
    axios.post('https://blog-e314c.firebaseio.com/posts.json', newPost)
      .then(res => {
        const id = Date.now();
        const post = {
          ...newPost,
          id
        };
        const { posts } = this.state;
        posts.push(post);
        this.setState({ posts });

        console.log('RES: ', res);
      }).catch(err => {
        console.log('ERR: ', err);
      });
  }

  postSelectedHandler = (id) => {
    this.setState({ selectedPostId: id })
    console.log(this.state);
  }

  render() {
    return (
      <div className="App">
        <section className="Posts">
          {this.state.posts.map((el) => (
            <Post
              key={el.id}
              title={el.title}
              clicked={() => this.postSelectedHandler(el.id)} />
          ))}
        </section>

        <section>
          <AddPost
            onSubmit={this.postDataHundler} />
        </section>

        <section>
          <FullPost id={this.state.selectedPostId} />
        </section>
      </div>
    )
  }
}

export default App;
