import React, { Component, useRef } from 'react';
import './NewPost.css';
import axios from 'axios';

const initialState = {
    title: '',
    content: '',
    image_path: '',
}

const NewPost = props => {
    const titleInputRef = useRef();
    const contentInputRef = useRef();

    const postDataHundler = () => {

        const post = {
            title: titleInputRef.current.value,
            content: contentInputRef.current.value,
            image_path: ''
        }

        axios.post('https://blog-e2780.firebaseio.com/posts.json', post)
            .then(res => {
                console.log('RES: ', res);
                props.history.push('/');
            }).catch(err => {
                console.log('ERR: ', err);
            });

        //this.init();
    }

    return (
        <div className="NewPost">
            <h1>Add a Post</h1>
            <label>Title</label>
            <input
                type="text"
                value={this.state.title}
                onChange={(event) => this.setState({ title: event.target.value })}
            />
            
            <label>Content</label>
            <textarea 
            rows="4" 
            value={this.state.content} onChange={(event) => this.setState({ content: event.target.value })} />
            <button onClick={this.postDataHundler}>Add Post</button>
        </div>
    );

