import React, { Component } from 'react';
import './App.css';
import AddPost from './components/AddPost';
//import Person from './Person/Person';
import FullPost from './components/FullPost';
import Post from './components/Post';
import Button from '@material-ui/core/Button';
import AppBar from '@material-ui/core/AppBar';
import Todo from './components/Todo';
import Header from './components/Header';
import Auth from './components/Auth';
import axios from 'axios';

class App extends Component {

  state = {
    posts: [],
    selectedPostId: null
  }

  componentDidMount() {
    axios.get('https://blog-e2780.firebaseio.com/posts.json')
      .then(res => {
        const postsData = res.data;

        const posts = [];
        for (const key in postsData) {
          posts.push({ id: key, title: postsData[key].title, content: postsData[key].content })
        }

        this.setState({ posts: posts });
        console.log('RES: ', posts);
      }).catch(err => {
        console.log('ERR: ', err);
      });
  }

  postDataHundler = (newPost) => {
    axios.post('https://blog-e2780.firebaseio.com/posts.json', newPost)
      .then(res => {
        const id = Date.now();
        const post = {
          ...newPost,
          id
        };
        const { posts } = this.state;
        posts.push(post);
        this.setState({ posts });

        console.log('RES: ', res);
      }).catch(err => {
        console.log('ERR: ', err);
      });
  }

  postSelectedHandler = (id) => {
    this.setState({ selectedPostId: id })
    console.log(this.state.selectedPostId);
  }

  render() {
    return (
      <div className="App">
        <header>
          <nav>
            <ul>
              <li><a href="/"></a></li>
            </ul>
          </nav>
        </header>
        <section>
          <AddPost
            onSubmit={this.postDataHundler} />
        </section>

        <section className="Posts">
          {this.state.posts.map((el) => (
            <Post
              key={el.id}
              title={el.title}
              clicked={() => this.postSelectedHandler(el.id)} />
          ))}
        </section>

        <hr />

        <section>
          <FullPost id={this.state.selectedPostId} />
        </section>
      </div>
    )
  }
}

export default App;
