import React, {Component} from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    person: {name: 'Kaito', age:26}
  }

  submitHundler = (newName) => {
    this.setState({
      person: {name: newName, age: 26}
    })
  }

  nameChangeHandler = (event) => {
    this.setState({
      person: {name: event.target.value, age: 28}
    })
  }

  render() {
    return (
      <div className="App">
        <h1>Hello World!</h1>
        <button onClick={this.submitHundler.bind(this, 'Zeno')}>Submit</button>
        <Person name={this.state.person.name} age={this.state.person.age} />
      </div>
   );
  }

}

export default App;
