import React, { Component } from 'react';
import axios from 'axios';

class Blog extends Component {

  postDataHundler = (newPost) => {
    axios.post('https://blog-e2780.firebaseio.com/posts.json', newPost)
      .then(res => {
        const id = Date.now();
        const post = {
          ...newPost,
          id
        };
        const { posts } = this.state;
        posts.push(post);
        this.setState({ posts });

        console.log('RES: ', res);
      }).catch(err => {
        console.log('ERR: ', err);
      });
  }

  

  render() {
    return (
        <div className="Blog">
          <header>
            <nav>
              <ul>
                <li><a href="/">Home</a></li>
                <li><a href="/new-post">New Post</a></li>
              </ul>
            </nav>
          </header>

          

          {/* <section>
            <AddPost
              onSubmit={this.postDataHundler} />
          </section>

          <hr />

          <section>
            <FullPost id={this.state.selectedPostId} />
          </section> */}
        </div>
    )
  }
}

export default Blog;
