import React, { useState, useEffect, useReducer } from 'react';
import axios from 'axios';

const Todo = props => {
    const [todoName, setTodoName] = useState('');

    //useState instead useReducer
    //const [todoList, setTodoList] = useState([]);
    const [submittedTodo, setSubmittedTodo] = useState(null);

    const todoListReducer = (state, action) => {
        switch (action.type) {
            case 'ADD':
                return state.concat(action.payload);
            case 'SET':
                return action.payload;
            case 'REMOVE':
                return state.filter((todo) => todo.id !== action.payload);
            default:
                return state;
        }
    }

    const [todoList, dispatch] = useReducer(todoListReducer, []);

    useEffect(() => {
        axios.get('https://todo-list-bb387.firebaseio.com/todos.json')
            .then(result => {
                console.log(result);

                const todoData = result.data;
                const todos = [];
                for (const key in todoData) {
                    todos.push({ id: key, name: todoData[key].name })
                }

                //useState unstead useReducer
                //setTodoList(todos);

                //using useReducer
                dispatch({ type: 'SET', payload: todos });
            });
        return () => {
            console.log('Cleanup')
        }
    }, []);

    useEffect(() => {
        if (submittedTodo)
            //using useState
            //setTodoList(todoList.concat(submittedTodo));

            dispatch({ type: 'ADD', payload: submittedTodo });
    }, [submittedTodo]);


    const inputChangeHandler = event => {
        setTodoName(event.target.value);
    }

    const todoAddHandler = () => {
        axios.post('https://todo-list-bb387.firebaseio.com/todos.json', { name: todoName })
            .then(res => {
                console.log(res);

                setTimeout(() => {

                    const todoItem = { id: res.data.name, name: todoName };
                    setSubmittedTodo(todoItem);
                }, 3000);

            })
            .catch(err => {
                console.log(err);
            });
    }

    const todoRemoveHandler = todoId => {
        axios.delete(`https://todo-list-bb387.firebaseio.com/todos.json`)
        dispatch({ type: 'REMOVE', payload: todoId });
    }

    return (
        <React.Fragment>
            <input type="text" placeholder="Todo" onChange={inputChangeHandler} value={todoName} />
            <button type="button" onClick={todoAddHandler}>Add</button>
            <ul>
                {todoList.map(todo => (
                    <li key={todo.id} onClick={todoRemoveHandler.bind(this, todo.id)}>
                        {todo.name}
                    </li>
                ))}
            </ul>
        </React.Fragment>
    );
};

export default Todo;
