import React, { useState, useEffect, useReducer } from 'react';
import axios from 'axios';

const Todo = props => {
    const [todoName, setTodoName] = useState('');
    const [submittedTodo, setSubmittedTodo] = useState(null);
    //const [todoList, setTodoList] = useState([]);

    const todoListReducer = (state, action) => {
        switch (action.type) {
            case 'ADD':
                return state.concat(action.payload);
            case 'SET':
                return action.payload;
            case 'REMOVE':
                return state.filter((todo) => todo.id !== action.payload);
            default:
                return state;
        }
    };

    const [todoList, dispatch] = useReducer(todoListReducer, []);

    useEffect(() => {
        axios.get('https://todo-list-bb387.firebaseio.com/todos.json').then(res => {
            console.log('DATA: ', res);

            const todoData = res.data;
            const todos = [];
            for (const key in todoData) {
                todos.push({ id: key, name: todoData[key].name })
            }
            
            //setTodoList(todos); using dispatch
            dispatch({type: 'SET', payload: todos});

        });
    }, []);

    const inputChangeHandler = event => {
        setTodoName(event.target.value);
    };

    const todoAddHundler = () => {
        axios.post('https://todo-list-bb387.firebaseio.com/todos.json', { name: todoName })
            .then(res => {
                const todoItem = { id: res.data.name, name: todoName };
                //setSubmittedTodo(todoItem);

                dispatch({type: 'ADD', payload: todoItem});

                console.log('RES: ', res);
                console.log('todoItem: ', todoItem);
            }).catch(err => {
                console.log('ERR: ', err);
            });
    };

    const todoRemoveHundler = todoId => {
        axios.remo

        dispatch({type: 'REMOVE', payload: todoId});
    };

    return [
        <input key="input"
            type="text"
            placeholder="Todo"
            onChange={inputChangeHandler}
            value={todoName}
        />,
        <button key="btn" type="button" onClick={todoAddHundler}>Add</button>,
        <ul key="ul">
            {todoList.map(todo => (
                <li key={todo.id} onClick={todoRemoveHundler.bind(this, todo.id)} >{todo.name}</li>
            ))}
        </ul>
    ]

};

export default Todo;
