import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Todo = props => {
    const [todoName, setTodoName] = useState('');
    const [submittedTodo, setSubmittedTodo] = useState(null);
    const [todoList, setTodoList] = useState([]);

    const todoListReducer = (state, action) => {}

    useEffect(() => {
        axios.get('https://todo-list-bb387.firebaseio.com/todos.json').then(res => {
            console.log('DATA: ',res);
            
            const todoData = res.data;
            const todos = [];
            for(const key in todoData) {
                todos.push({id: key, name: todoData[key].name})
            }
            setTodoList(todos);
        });
    }, []);

    const inputChangeHandler = event => {
        setTodoName(event.target.value);
    };

    useEffect(() => {
        if(submittedTodo) setTodoList(todoList.concat(submittedTodo));
    }, [submittedTodo])

    const todoAddHundler = () => {
        axios.post('https://todo-list-bb387.firebaseio.com/todos.json', {name: todoName})
        .then(res => {
            const todoItem = { id: res.data.name, name: todoName };
            setSubmittedTodo(todoItem);
            console.log('RES: ', res);
        }).catch(err => {
            console.log('ERR: ', err);
        });
    }

    return [
        <input key="input"
            type="text"
            placeholder="Todo"
            onChange={inputChangeHandler}
            value={todoName}
            />,
        <button key="btn" type="button" onClick={todoAddHundler}>Add</button>,
        <ul key="ul">
            {todoList.map(todo => (
                <li key={todo.id}>{todo.name}</li>
            ))}
        </ul>
    ]

};

export default Todo;
