      })
    } */

  state = {
    posts: []
  }

  postDataHundler = (newPost) => {
    const id = Date.now();
    const post = {...newPost, 
      id
    };
    const { posts } = this.state;
    posts.push(post);
    this.setState({ posts });

    console.log('New Post', post);
  }


  render() {
    console.log('state', this.state);
    return (
      <div className="App">


        <section>
          <AddPost
            onSubmit={this.postDataHundler} />
        </section>

        <section>
          {this.state.posts.map((el) => (
            <FullPost
              key={el.id}
              title={el.title}
              content={el.content} />
          ))}
        </section>
      </div>
    );
  }

}

export default App;
