import React, {Component} from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    person: {name: 'Kaito', age:26}
  }

  submitHundler = () => {
    this.setState({
      person: {name: 'Zeno', age: 26}
    })
  }

  render() {
    return (
      <div className="App">
        <h1>Hello World!</h1>
        <button onClick={this-submitHundler}>Submit</button>
        <Person name={this.state.person.name} age={this.state.person.age} />
      </div>
   );
  }

}

export default App;
