import React, { Component } from 'react';
import './App.css';
import AddPost from './components/AddPost';
//import Person from './Person/Person';
//import FullPost from './components/FullPost';
import Post from './components/Post';
import Button from '@material-ui/core/Button';

class App extends Component {
  /*   state = {
      person: {name: 'Kaito', age:26}
    }

    submitHundler = (newName) => {
      this.setState({
        person: {name: newName, age: 26}
      })
    }

    nameChangeHandler = (event) => {
      this.setState({
        person: {name: event.target.value, age: 28}
      })
    } */

  state = {
    posts: [
      { id: 1, title: 'T1', content: 'C1', image_path: '' },
      { id: 2, title: 'T2', content: 'C2', image_path: '' },
      { id: 3, title: 'T3', content: 'C3', image_path: '' },
      { id: 4, title: 'T4', content: 'C4', image_path: '' }
    ]
  }

  postDataHundler = (newPost) => {
    const id = Date.now();
    const post = {
      ...newPost,
      id
    };
    const { posts } = this.state;
    posts.push(post);
    this.setState({ posts });

    console.log('New Post', post);
  }


  render() {
    console.log('state', this.state);
    return (
      <div className="App">
        <header>
          <Button variant="outlined" color="primary">
            Hello World
          </Button>
          <AppBar position="static">
  <Toolbar>
    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
      <MenuIcon />
    </IconButton>
    <Typography variant="h6" className={classes.title}>
      News
    </Typography>
    <Button color="inherit">Login</Button>
  </Toolbar>
</AppBar>
        </header>

        <section className="Posts">
          {this.state.posts.map((el) => (
            <Post
              key={el.id}
              title={el.title} />
          ))}
        </section>

        <section>
          <AddPost
            onSubmit={this.postDataHundler} />
        </section>

        {/* <section>
          {this.state.posts.map((el) => (
            <FullPost
              key={el.id}
              title={el.title}
              content={el.content} />
          ))}
        </section> */}
      </div>
    );
  }

}

export default App;
